call add_concept_word(@concept_id, @concept_name_short_id, 'Deny', 1);
call add_concept_word(@concept_id, @concept_name_short_id, 'Admission', 1);

insert into concept_reference_map (concept_reference_term_id, concept_map_type_id, concept_id, date_created, creator) values (
@concept_reference_term_id , @concept_map_type_id, @concept_id, @current_date, 1);

